import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const Task = (props) => {

    return (
        <View style= {styles.item}>
          <View style={styles.itemLeft}>
           <View style= {styles.square}></View>
           <Text style={styles.itemText}>{props.text}</Text>
          </View>
          <View style= {styles.circular}></View>
        </View>
    )
}

const styles = StyleSheet.create({
    item:{
        backgroundColor:'#fff',
        padding: 15,
        borderRadius: 10,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        marginBottom: 20,
    },
    itemLeft:{
        flexDirection: 'row',
        alignItems: 'center',
        flexWrap: 'wrap',
        
    },
    square:{
        width: 24,
        height: 24,
        backgroundColor: '#67C0B7',
        opacity: 0.4,
        borderRadius: 5,
        marginRight: 10,

    },
    itemText:{},
    circular:{
        borderColor: '#67C0B7',
        borderWidth: 10,

    },
});

export default Task;
